mapper.select:
    SELECT BOARD_NO,BOARD_CODE, CATEGORY, 
    WRITE_DATE , 
    WRITER, TITLE, CONTENT,MANDATORY 
    FROM BOARD
	WHERE BOARD_CODE = #{boardCode}
	AND CATEGORY = #{category}

